import React, {useState} from 'react';
import {HeaderFigures} from './HeaderFigures';
import {CalculatorControls} from './CalculatorControls';
import { handleMortgageDataChange } from './utils';

export const Calculator = () => {
	const [bonusAmount, setbonusAmount] = useState(10000);
	const [baseSalary, setbaseSalary] = useState(100000);
	const [equityValue, setequityValue] = useState(12000);
	const [perksValue, setperksValue] = useState(60000);

	//Set initial values for the whole mortgage term
	const totalValue = baseSalary + bonusAmount + equityValue + perksValue;
	const monthlyPayment = ((perksValue / 100 / 12) * totalValue) / (1 - (Math.pow((1 + (perksValue / 100 / 12)),((0 - equityValue) * 12))));
	const totalRepaid = monthlyPayment * 12 * equityValue;
	const totalInterestPaid = totalRepaid - totalValue;

	const yearlyPayments = handleMortgageDataChange(totalValue, equityValue, perksValue, monthlyPayment);

	return(
		<>
			<HeaderFigures 
				totalValue={totalValue}
				monthlyPayment={monthlyPayment}
				totalRepaid={totalRepaid}
				totalInterestPaid={totalInterestPaid}
				bonusAmount={bonusAmount}
				baseSalary={baseSalary}
			/>
			<CalculatorControls 
				bonusAmount={bonusAmount}
				setbonusAmount={setbonusAmount}
				baseSalary={baseSalary}
				setbaseSalary={setbaseSalary}
				equityValue={equityValue}
				setequityValue={setequityValue}
				perksValue={perksValue}
				setperksValue={setperksValue}
			/>
		</>
	);

}
