import React, {useState} from 'react';
import {HeaderFigures} from './HeaderFigures';
import CalculatorControls from './CalculatorControls';


export default class Calculator extends React.Component{

	constructor() {
		super();

		this.state = {
			baseSalary: window.baseSalary,
			bonusAmount: window.bonusAmount,
			equityValue: window.equityValue,
			perksValue: window.perksValue,
			totalValue: window.baseSalary + window.bonusAmount + window.perksValue + window.equityValue
		};
	}

	render() {
		return (
			<>
				<HeaderFigures
					totalValue={totalValue}
					perksValue={perksValue}
					bonusAmount={bonusAmount}
					baseSalary={baseSalary}
				/>
				<CalculatorControls
				/>
			</>
		);
	}
}
